import { createSlice } from "@reduxjs/toolkit";

export const todosSlice = createSlice({
  name: "todos",
  initialState: {
    items: [
      {
        id: 1,
        title: "Learn React",
        completed: true,
      },
      {
        id: 2,
        title: "Read a book",
        completed: false,
      },
    ],
  },
  reducers: {
    addTodo: (state,action) => {
      state.items.push({id:"3",title:"test",completed: true});
    }
  },
});

export default todosSlice.reducer;
