import React from "react";
import { useSelector,useDispatch } from "react-redux";
import {changeActiveFilter} from "../../redux/todos/todosSlice"


function ContentFooter() {
  const dispatch = useDispatch();
  const items = useSelector((state) => state.todos.items);
  const itemsLeft = items.filter((item) => !item.completed).length;
  const activeFilter = useSelector((state) => state.todos.activeFilter);

  return (
    <footer className="footer">
      <span className="todo-count">
        <strong>{itemsLeft}</strong> item{itemsLeft > 1 && "s"} left
      </span>
      <ul className="filters">
        <li>
          <a href="#/" className={activeFilter === "all" ? "selected" : ""} onClick={() => dispatch(changeActiveFilter("all"))}>
            All
          </a>
        </li>
        <li>
          <a href="#/" className={activeFilter === "all" ? "selected" : ""} onClick={() => dispatch(changeActiveFilter("active"))}>
            Active
          </a>
        </li>
        <li>
          <a href="#/" className={activeFilter === "all" ? "selected" : ""} onClick={() => dispatch(changeActiveFilter("completed"))}>
            Completed
          </a>
        </li>
      </ul>
      <button className="clear-completed">Clear completed</button>
    </footer>
  );
}

export default ContentFooter;
